$(document).ready (
	function() {
		webformCssClearUp();
		displayInlineContent();
	}
); 

function displayInlineContent() {
	var contentText = $("#dependant-child-definition");
	
	$("#fco_doyouhaveanydependantchildren_label").append(contentText);
	contentText.show();
}

function webformCssClearUp() {
	// This Class is auto-generated by CRM but we don't want it here
	$("#WebFormPanel .actions div.col-sm-6.clearfix").removeClass("col-sm-6");
}